var app = angular.module('App', ['ngRoute']);

app.config(function ($routeProvider) {
    $routeProvider
        .when('/', {
            templateUrl: '/views/main.html',
            controller: 'mainController'
        });
});
angular.module('App')
    .controller('mainController', function ($scope) {
        $scope.message = "We have lift off!";
    });